Article.create(:id => 46, :title => "Глава Barclays Боб Даймонд уволился из-за скандала с ЛИБОР", :url => "http://news.yandex.ru/yandsearch?cl4url=www.bfm.ru%2Fnews%2F2012%2F07%2F03%2Fglava-barclays-bob-dajmond-uvolilsja-iz-za-skandala-s-libor.html&amp;cat=6&amp;lang=ru", :text => "Президент Barclays Боб Даймонд уходит в отставку на фоне скандала с махинациями со ставками межбанковского кредитования, сообщает Reuters со ссылкой на заявление банка. Накануне банк покинул председатель совета директоров Маркус Эйджиус.", :feed_id => 257)
Article.create(:id => 47, :title => "ЦБ РФ надеется удержать инфляцию на уровне 5-6% по итогам года", :url => "http://news.yandex.ru/yandsearch?cl4url=www.rosbalt.ru%2Fbusiness%2F2012%2F07%2F03%2F1006462.html&amp;cat=6&amp;lang=ru", :text => "Банк России все еще надеется уложиться в целевой ориентир по инфляции на 2012 год в 5-6%. Как передает &amp;quot;Вести Экономика&amp;quot;, об этом сегодня заявил на брифинге зампред ЦБ РФ Сергей Швецов.", :feed_id => 257)
Article.create(:id => 48, :title => "В детском лагере в Ленобласти 26 детей отравились салатом", :url => "http://news.yandex.ru/yandsearch?cl4url=www.interfax.ru%2Ftourism%2Ftourisminf.asp%3Fid%3D253670%26sec%3D1466&amp;cat=6&amp;lang=ru", :text => "В детском лагере &amp;quot;Факел +&amp;quot; в Ленинградской области острой кишечной инфекцией заболели 26 отдыхающих, сообщает пресс-служба регионального Роспотребнадзора. Вспышка инфекции произошла в детском оздоровительном лагере &amp;quot;Факел +&amp;quot; в период с 28 по 29 июня.", :feed_id => 257)
Article.create(:id => 49, :title => "Словения может обратиться за финансовой помощью в ЕС", :url => "http://news.yandex.ru/yandsearch?cl4url=www.finmarket.ru%2Fz%2Fnws%2Fnews.asp%3Fid%3D2980096&amp;cat=6&amp;lang=ru", :text => "Ранее сообщалось, что Греция, Ирландия, Португалия, Испания и Кипр были вынуждены обратиться за финансовой помощью в Евросоюз после того, как стоимость их заимствований выросла. Премьер Словении Янез Янша 27 июня заявил, что страна рискует повторить &amp;quot;греческий сценарий&amp;quot;.", :feed_id => 257)
Article.create(:id => 50, :title => "Microsoft понесет издержки $6,2 млрд из-за неудачных инвестиций", :url => "http://news.yandex.ru/yandsearch?cl4url=www.dp.ru%2Fa%2F2012%2F07%2F03%2FMicrosoft_spishet_62_mlr%2F&amp;cat=6&amp;lang=ru", :text => "Корпорация Microsoft планирует списать $6,2 млрд из прибыли четвертого квартала текущего финансового года, чтобы отразить расходы на неудачные вложения в интернет-сектор.", :feed_id => 257)
Article.create(:id => 51, :title => "Замглавы МЭР Осеевский освобожден от должности", :url => "http://news.yandex.ru/yandsearch?cl4url=www.ria.ru%2Feconomy%2F20120703%2F690798255.html&amp;cat=6&amp;lang=ru", :text => "Премьер-министр РФ Дмитрий Медведев освободил Михаила Осеевского от должности заместителя министра экономического развития РФ по просьбе самого М. Осеевского. Соответствующее распоряжение правительства РФ от 29 июня 2012г. N1104-р подписано главой кабинета министров.", :feed_id => 257)
Article.create(:id => 52, :title => "Курс доллара снизился на 70 копеек", :url => "http://news.yandex.ru/yandsearch?cl4url=www.rg.ru%2F2012%2F07%2F03%2Fdollar.html&amp;cat=6&amp;lang=ru", :text => "Евро отступил на 73 копейки, до 41,32 рубля. По итогам июня рубль заметно укрепился к доллару и немного вырос по отношению к евро: американская валюта отступила за месяц на ММВБ примерно на 1 рубль, в то время как снижение евро не превысило 30 копеек.", :feed_id => 257)
Article.create(:id => 53, :title => "Илья Массух покинул пост замглавы Минкомсвязи РФ", :url => "http://news.yandex.ru/yandsearch?cl4url=www.ria.ru%2Feconomy%2F20120703%2F690781146.html&amp;cat=6&amp;lang=ru", :text => "Илья Массух покинул пост заместителя главы Минкомсвязи РФ, соответствующее распоряжение правительства размещено во вторник в банке федеральных нормативных и распорядительных актов. Массух ранее заявлял о намерении покинуть министерство.", :feed_id => 257)
Article.create(:id => 54, :title => "Лужков считает поспешным решение о расширении границ Москвы", :url => "http://news.yandex.ru/yandsearch?cl4url=riarealty.ru%2Fnews%2F20120703%2F398018687.html&amp;cat=6&amp;lang=ru", :text => "Кремль вы никуда не перенесете, Белый дом вы никуда не перенесете, взяли да разместили разное количество громадных министерств где-то там на границе с Калужской областью.", :feed_id => 257)
Article.create(:id => 55, :title => "Юргенс возглавит совет директоров Русской агропромышленной корпорации", :url => "http://news.yandex.ru/yandsearch?cl4url=www.ria.ru%2Fcompany%2F20120703%2F690704396.html&amp;cat=6&amp;lang=ru", :text => "Директор Института современного развития ( ИНСОР ) Игорь Юргенс в ближайшее время займет пост председателя совета директоров Русской агропромышленной корпорации. Об этом сам Юргенс рассказал в интервью &amp;quot;Известиям&amp;quot;.", :feed_id => 257)
Article.create(:id => 56, :title => "Российский премьер посетил Дальний Восток", :url => "http://news.yandex.ru/yandsearch?cl4url=www.oblgazeta.ru%2Fpolitics%2F700%2F&amp;cat=6&amp;lang=ru", :text => "Многое из сделанного в последние годы во Владивостоке, связано с проведением саммита АТЭС, признал Дмитрий Медведев на церемонии открытии моста, но подчеркнул, что &amp;quot;это не для саммита, это для вас — для всех, кто живет здесь&amp;quot;.", :feed_id => 257)
